#include <cstdio>

int main() {
  int  N;
  scanf("%d", &N);
  int tab[2000];
  for (int i = 0; i < N; ++i) scanf("%d", &tab[i]);
  int rot = N - tab[0];
  for (int i = 0; i < N; ++i) {
    if (i & 1) tab[i] += (N - rot); else tab[i] += rot;
    tab[i] %= N;
    if (tab[i] != i) {
      printf("No\n"); return 0;
    }
  }
  printf("Yes\n"); return 0;
}
