#include<cstdio>
#include<cstring>
#include<algorithm>
using namespace std;
struct str{int x,y;}a[2100],b[2100];
int n,m,l,r,cnt[2100],ans,i,k,j;
int main()
{
	scanf("%d%d%d%d",&n,&m,&l,&r);
	for(i=1;i<=n;i++)
		scanf("%d%d",&a[i].x,&a[i].y);
	for(i=1;i<=m;i++)
		scanf("%d%d",&b[i].x,&b[i].y);
	ans=0;
	for(i=l;i<=r;i++)
	{
		for(j=0;j<=2000;j++)cnt[j]=0;
		for(j=1;j<=n;j++)
			for(k=a[j].x;k<=a[j].y;k++)
				cnt[k]=1;
		for(j=1;j<=m;j++){
			for(k=i+b[j].x;k<=i+b[j].y;k++)
				if(cnt[k])break;
			if(k<=i+b[j].y)break;
		}
		if(j<=m)ans++;
	}
	printf("%d\n",ans);
}
