#include <bits/stdc++.h>
using namespace std;

typedef long long i64;

i64 inverse(int x)
{
    i64 ans;
    i64 base(1);
    while (x >= base)
        base *= 10;
    ans = base * x;
    base /= 10;
    while (x) {
        ans += (x % 10) * base;
        x /= 10;
        base /= 10;
    }
    return ans;
}

int main()
{
    //cout << inverse(1);
    i64 ans(0);
    int n, k;
    cin >> n >> k;
    for (int i(1); i <= n; i++)
        ans += inverse(i);

    cout << ans % (1ll * k);
    return 0;
}
