#include<cstdio>
#include<iostream>
#include<queue>
#include<map>
#include<set>
#include<algorithm>
#include<cmath>
#include<cstdlib>
using namespace std;

#define inf 1023456789
#define linf 1023456789123456789ll
#define pii pair<int,int>
#define pipii pair<int, pii >
#define pll pair<long long,long long>
#define vint vector<int>
#define vvint vector<vector<int> >

#define DEBUG
#ifdef DEBUG
#define db(x) cerr << #x << " = " << x << endl
#else
#define db(x)
#endif

int main()
{
	int f[3];
	int mn = inf;
	for(int i=0; i<3; i++)
	{
		scanf("%d",&f[i]);
		mn = min(mn, f[i]);
	}
	int res = 0;
	for(int i=0; i<=min(2, mn); i++)
	{
		int pom = i;
		for(int j=0; j<3; j++)pom += (f[j] - i)/3;
		res = max(res,pom);
	}
	printf("%d\n",res);
	return 0;
}
