#include <bits/stdc++.h>
using namespace std;

int main() {
    ios::sync_with_stdio(false); cin.tie(0);
    int T, n;
    cin >> T;
    while (T--) {
        cin >> n;
        vector<int> a(n);
        for (int i = 0; i < n; i++) cin >> a[i];
        if (n % 2 == 1) {
            cout << "Mike" << endl;
            continue;
        }
        int mn = 2e9, pos;
        for (int i = 0; i < n; i++) {
            if (a[i] < mn) {
                mn = a[i];
                pos = i;
            }
        }
        if (pos % 2 == 0) {
            cout << "Joe" << endl;
        } else {
            cout << "Mike" << endl;
        }
    }
    return 0;
}
