#include<bits/stdc++.h>
using namespace std;
int read() {
	int x=0,f=1;char c=getchar();
	while(c>'9'||c<'0') {if(c=='-')f=-1;c=getchar();}
	while(c>='0'&&c<='9') {x=x*10+c-'0';c=getchar();}
	return x*f;
}
void work() {
	int n=read(),m=read(),ans=0;
	if(n>m)swap(n,m);
	if(n%3==0)printf("%d\n",n/3*m);
	else if(m%3==0)printf("%d\n",m/3*n);
	else {
		ans+=(m/3)*n;m%=3;
		if(m==2) {
			ans+=n/3*2;
			n%=3;
			if(n==1)++ans;
			else ans+=2;
		}
		else if(m==1) {
			ans+=n/3;
			n%=3;
			if(n)++ans;
		}
		printf("%d\n",ans);
	}
}
int main() {
	int T=read();
	while(T--)work();
	return 0;
}
