#include <cstdio>
const	int	maxn = 220000;
char	a[maxn];
int	n, f1, f2, f3;
int	main()
{
	scanf("%d", &n);
	scanf("%s", a);
	f1 = f2 = f3 = 0;
	for (int i=0; i<n; i++)
	{
		switch(a[i])
		{
			case 'A': f1 ++; break;
			case 'F': f2 ++; break;
			case 'I': f3 ++; break;
		}
	}
	if (f3 > 1) {printf("0\n"); return 0;}
	if (f3 == 1) {printf("1\n"); return 0;}
	printf("%d\n", f1);
	return 0;
}
