#include <stdio.h>
int a[110], b[110];
int l[210], w, r;
int main() {
    int n, m, x, y, p, i;
    scanf("%d%d", &n, &m);
    scanf("%d", &p);
    while (p--) {
        scanf("%d", &x);
        a[x] = 1;
        l[w++] = x;
    }
    scanf("%d", &p);
    while (p--) {
        scanf("%d", &x);
        b[x] = 1;
        l[w++] = n + x;
    }
    for (r = 0; r < w; r++) {
        x = l[r];
        if (x < n) {
            y = x % m;
            do {
                if (!b[y]) {
                    b[y] = 1;
                    l[w++] = n + y;
                }
                y = (y + n) % m;
            } while (y != x % m);
        } else {
            x = x - n;
            y = x % n;
            do {
                if (!a[y]) {
                    a[y] = 1;
                    l[w++] = y;
                }
                y = (y + m) % n;
            } while (y != x % n);
        }
    }
    for (i = 0; i < n; i++) if (!a[i]) {
        printf("No\n"); return 0;
    }
    for (i = 0; i < m; i++) if (!b[i]) {
        printf("No\n"); return 0;
    }
    printf("Yes\n");
    return 0;
}
