using namespace std;
typedef long long int lli;

int gInt () {
  int i;
  scanf("%d", &i);
  return i;
}

lli gLong () {
  lli i;
  scanf("%lld", &i);
  return i;
}

double gDouble () {
  double i;
  scanf("%lf", &i);
  return i;
}

void quit () {
  fflush(stdout);
  exit(0);
}

int main (int argc, char ** argv) {
  int num = gInt();
  double sum = 0.0;
  for (int count = 0; count < num; count ++)
    sum += gDouble();
  printf("%.13lf\n", sum / num);
  quit();
}
