typedef long long ll;
typedef pair<int,int> pii;
typedef vector<int> vi;
typedef vector< vi > vii;

#define N 2000009

bool p[N];
int pc, pr[N];

void crivo(int n) {
	cl(p, 1);
	p[0] = p[1] = 0;

	for(int i = 2; i*i <= n; i++) if (p[i]) {
		for(int j = i*i; j <= n; j += i) p[j] = 0;
	}

	pc = 0;
	fr(i, 2, n+1) if (p[i]) pr[pc++] = i;
}

int main() {
	crivo(N-4);

	ll n;
	while (scanf("%I64d", &n) == 1) {
		int j = 0;
		ll ans = 1;
		while(j < pc && n > 1) {
			if (n%pr[j] == 0) {
				ans *= pr[j];
				while (n%pr[j] == 0) n /= pr[j];
			}
			j++;
		}
		ans *= n;
		cout << ans << '\n';
	}

	return 0;
}
