using namespace std;
const double eps=1e-8;
const double pi=acos(-1.0);
int dblcmp(double d){if (fabs(d)<eps)return 0;return d>eps?1:-1;}
typedef long long ll;
typedef vector<int> vi;
typedef vector<ll> vl;
typedef vector<string> vs;
typedef pair<int,int> pii;
typedef vector<pii> vpi;
int main()
{
    int i,j,k;
    double v1,v2,t,f,c;
    cin>>v1>>v2>>t>>f>>c;
    if (dblcmp(v1-v2)>=0)
    {
        puts("0");
        return 0;
    }
    double x=0,y=0;
    int ans=0;
    x+=v1*t;
    while (dblcmp(x-c)<0)
    {
        double d=x-y;
        double s=d/(v2-v1);
        x+=v1*s;
        y+=v2*s;
        if (dblcmp(x-c)>=0)
        {
            printf("%d\n",ans);
            return 0;
        }
        ans++;
        x+=v1*(f+y/v2);
        y=0;
    }
    printf("%d\n",ans);
    return 0;
}
