#include <iostream>
#include<cstdio>
#include<cstring>

using namespace std;

int c[200000],t[200000];

int main()
{
	int n,m;
	cin >> n >> m;
	for (int i = 0 ; i < n ; i++)
	{
		scanf("%d %d",&c[i], &t[i]);
	}
	int j = 0;
	int ot = 0 ;
	for (int i = 0 ; i  < m; i++)
	{
		int tt;
		scanf("%d", &tt);
		while (ot + c[j] * t[j] < tt)
		{
			ot = ot + c[j] * t[j];
			j++;
		}
		cout << j + 1 << endl;
	}
	//system("pause");
}
