#include <bits/stdc++.h>
using namespace std;

const int N = 330000;

int a[N];

int main() {
#ifndef ONLINE_JUDGE
	freopen("in.txt", "r", stdin);
	freopen("out.txt", "w", stdout);
#endif
	ios_base::sync_with_stdio(0);
	cin.tie(0); cout.tie(0);
	int n; cin >> n;
	for (int i = 1; i <= n; i++) cin >> a[i];
	int ans = INT_MAX;
	for (int i = 1; i <= n; i++) {
		if (i > 1) ans = min(ans, a[i] / (i - 1));
		if (i < n) ans = min(ans, a[i] / (n - i));
	}
	cout << ans << endl;
	return 0;
}
