#include <bits/stdc++.h>
using namespace std;
int n, m, k, h[1000020];
bool fall;
int bone, x, y;
int main() {
	scanf("%d%d%d", &n, &m, &k);
	bone = 1;
	for (int i = 0; i < m; i++) {
		scanf("%d", &x);
		h[x] = 1;
	}
	if (h[1] == 1) {
		fall = true;
	}
	for (int i = 0; i < k; i++) {
		scanf("%d%d", &x, &y);
		if (fall) {
			continue;
		}
		if (x == bone) {
			bone = y;
		} else if (y == bone) {
			bone = x;
		}
		if (h[bone] == 1) {
			fall = true;
		}
	}
	printf("%d\n", bone);
}
