#include <iostream>
#include <algorithm>
using namespace std;
int arr[256];
int n;
int main()
{
	cin>>n;
	for(int i=0; i<n; ++i) cin>>arr[i];
	int cnt=0;
	for(int i=0; i<n; ++i) cnt += arr[i]&1;
	if (!cnt) {
		cout<<0<<'\n';
		return 0;
	}
	int r=0;
	sort(arr,arr+n);
	for(int i=0; i<n; ++i) r+=arr[i];
	int a=0;
	while(!(arr[a]&1)) ++a;
	if (!(cnt&1)) r -= arr[a];
	cout<<r<<'\n';
}
