#include <bits/stdc++.h>
using namespace std;

const int max_n = 2e5 + 5;

int a[max_n];

void solve_tc(){
	int n; 
	cin >> n;
	for (int i = 1; i <= n; i++){
		cin >> a[i];
	}

	int operations = 0;
	while (1){
		int similar_suffix = 0;
		for (int i = n; i >= 1; i--){
			if (a[i] != a[n]){
				break;
			}
			similar_suffix = n - i + 1;
		}
		if (similar_suffix == n){
			break;
		}
		for (int i = max(1, n - 2 * similar_suffix + 1); i <= n; i++){
			a[i] = a[n];
		}
		operations++;
	}

	cout << operations << "\n";
}

int main(){
	ios_base::sync_with_stdio(0);
	cin.tie(0);

	int tests;
	cin >> tests;
	while (tests--){
		solve_tc();
	}
}
