#include <bits/stdc++.h>
using namespace std;

void solve() {
    int n;
    cin >> n;
    vector<long long> a(n);
    for (auto &x : a) {
        cin >> x;
    }
    partial_sum(a.begin(), a.end(), a.begin());
    long long ans = 0;
    for (int i = 0; i + 1 < n; i++) {
        ans = max(ans, gcd(a[i], a.back() - a[i]));
    }
    cout << ans << "\n";
}

int main() {
    ios::sync_with_stdio(0), cin.tie(0);
    int T;
    cin >> T;
    while (T--) {
        solve();
    }
    return 0;
}
