#include <iostream>

using namespace std;

bool fits(int a, int b, int p, int q, int u, int v) {
    return a >= p+u && b >= max(q,v);
}

int main() {
    int a, b;
    int p, q;
    int u, v;
    cin >> a >> b >> p >> q >> u >> v;
    if(fits(a,b,p,q,u,v) ||
        fits(a,b,p,q,v,u) ||
        fits(a,b,q,p,u,v) ||
        fits(a,b,q,p,v,u) ||
        fits(b,a,p,q,u,v) ||
        fits(b,a,p,q,v,u) ||
        fits(b,a,q,p,u,v) ||
        fits(b,a,q,p,v,u)) cout << "YES" << endl;
    else cout << "NO" << endl;
    return 0;
}
