#include <bits/stdc++.h>
using namespace std;
using i64 = long long;
#define N 300'005

int n, k, a[N];
void solve()
{
    cin >> n;
    for (int i = 1; i <= n; i++)
        cin >> a[i];
    int tmp = a[1];
    for (int i = 2; i <= n; i++)
        tmp &= a[i];
    if (tmp)
    {
        cout << 1 << '\n';
        return;
    }
    int ans = 0, pre = (1 << 30) - 1;
    for (int i = 1; i <= n; i++)
    {
        pre &= a[i];
        if (!pre)
        {
            ans++;
            pre = (1 << 30) - 1;
        }
    }
    cout << ans << '\n';
}

signed main()
{
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);
    int t = 1;
    cin >> t;
    while (t--)
        solve();
}
