#include<bits/stdc++.h>

using namespace std;

typedef long long ll;
typedef pair<int, int> pii;

#define F first
#define S second

const int MAXK = 1e5 + 10;

ll a[MAXK];

int main(){
	ios::sync_with_stdio(false);
	cin.tie(0);
	ll n; cin >> n;
	int k; cin >> k;
	for (int i = 0; i < k; i++) cin >> a[i];
	ll mx = -1, id = -1;
	for (int i = 0; i < k; i++){
		ll cur = n - n % a[i];
		if (cur > mx){
			mx = cur;
			id = i;
		}
	}
	cout << id+1 << " " << n/a[id] << "\n";
	return 0;
}
