#include <bits/stdc++.h>
#define int long long 
using namespace std;
const int INF=1e6+5;
int n,a[INF],p3,b[INF],p[INF];
void solve(){
	cin>>n>>p3;
	for (int i=1;i<=n;i++) cin>>a[i];
	for (int i=1;i<=n;i++) cin>>b[i],p[i]=i;
	sort(p+1,p+1+n,[](int x,int y){return b[x]<b[y];});
	int sum=0,res=0;
	for (int i=1;i<=n;i++) {
		if (sum<i) sum++,res+=p3;
		if (b[p[i]]<p3) {
			int L=min(n-sum,a[p[i]]);
			res+=L*b[p[i]];
			sum+=L;
		}
	}
	cout<<res<<"\n";
}
signed main()
{
	ios::sync_with_stdio(false);
	int t=0;cin>>t;
	while (t--) solve();
	return 0;
}
