using namespace std;

int n, sum, a[21000];
int main()
{
    scanf("%d", &n);
    sum = 0;
    for (int i = 0; i < n; i++) {
        scanf("%d", a + i);
        sum += a[i];
    }
    if (sum % n == 0) {
        sum /= n;
        int cc = 0;
        for (int i = 0; i < n; i++)
            if (a[i] == sum)
                cc++;
        if (cc == n)
            puts("Exemplary pages.");
        else if (cc == n - 2) {
            int l, r;
            for (int i = 0; i < n; i++)
                if (a[i] > sum)
                    l = i;
            for (int i = 0; i < n; i++)
                if (a[i] < sum)
                    r = i;
            printf("%d ml. from cup #%d to cup #%d.\n", a[l] - sum, r + 1, l + 1);
        } else
            puts("Unrecoverable configuration.");
    } else
        puts("Unrecoverable configuration.");
}
