#include<bits/stdc++.h>
#define rep(i,n) for (int i=1;i<=(n);i++)
using namespace std;
int n,l,a[100005],b[100005];
vector<int> v;
void solve()
{
	cin>>n>>l;
	rep(i,n) cin>>a[i];
	v.clear();
	rep(i,n) if(i-l>=1||i+l<=n) v.push_back(a[i]);
	sort(v.begin(),v.end());
	int t=0;
	rep(i,n) if(i-l>=1||i+l<=n) b[i]=v[t++]; else b[i]=a[i];
	rep(i,n-1) if(b[i]>b[i+1]) {puts("NO");return;}
	puts("YES");
}
int main()
{
	int t;
	cin>>t;
	while(t--) solve();
	return 0;
}
