#include <cmath>
#include <cstdio>
#include <cstdlib>
#include <iostream>
#include <algorithm>
#include <cstring>
using namespace std;

const int MAXN = 101000;

char str[MAXN];
int f[MAXN], n, m;

int main()
{
#ifndef ONLINE_JUDGE
	freopen("input.txt", "r", stdin);
	freopen("output.txt", "w", stdout);
#endif
	scanf("%s", str + 1);
	n = strlen(str + 1);
	scanf("%d", &m);
	for (int i = 1; i < n; ++i)
		f[i] = f[i - 1] + (str[i] == str[i + 1]);
	for (int i = 1; i <= m; ++i)
	{
		int x, y;
		scanf("%d%d", &x, &y);
		printf("%d\n", f[y - 1] - f[x - 1]);
	}
	fclose(stdin);
	fclose(stdout);
	return 0;
}
