#include<bits/stdc++.h>

using namespace std;

const int max_n=1e5+5;
int a[max_n];
int mark[max_n];
int main()
{
    int n;
    cin >> n;
    for(int i=0;i<n;i++)
    {
        cin>> a[i];
        mark[a[i]]++;
    }
    int po=1;
    for(int i=0;i<n;i++)
    {
        if(mark[a[i]]==1&& a[i]<=n)
            cout<< a[i]<< ' ';
        else
        {
            mark[a[i]]--;
            while(mark[po]!=0)
                po++;
            cout<< po<<' ';
            mark[po]++;
        }
    }
}
