#include<iostream>
#include<algorithm>
#include<set>
#include<map>
#include<vector>
#include<queue>
#include<bitset>
using namespace std;
typedef long long ll;
const int P= 0x3f3f3f3f;
void solve()
{
    int n, m, k, x, pt = 0;cin >> n >> m >> k;
    vector<int>a(n + 1), b(m + 1); vector<ll>val(200);
    ll sum = 0; for (int i = 1; i <= n; i++)cin >> a[i], sum += a[i];
    for (int i = 1; i <= m; i++)cin >> b[i];val[0] = sum;
    for (int i = 1; i <= k; i++)
    {
        int ma = -P, mi =P, mac, mic;
        if (i & 1)
        {
            for (int i = 1; i <= n; i++)if (mi > a[i])mi = a[i], mic = i;
            for (int i = 1; i <= m; i++)if (ma < b[i])ma = b[i], mac = i;
            if (ma > mi)sum += b[mac] - a[mic], swap(b[mac], a[mic]);
        }
        else
        {
            for (int i = 1; i <= m; i++)if (mi > b[i])mi = b[i], mic = i;
            for (int i = 1; i <= n; i++)if (ma < a[i])ma = a[i], mac = i;
            if (ma > mi)sum -= a[mac] - b[mic], swap(a[mac], b[mic]);
        }
        val[i] = sum;
        if (i >= 3 && val[i] == val[i - 2] && val[i - 1] == val[i - 3])
            {cout << (((k - i) & 1) ? val[i - 1] : val[i]) << '\n'; return;}
    }
    cout << val[k] << '\n';
}
int main()
{
    ios::sync_with_stdio(0);cin.tie(0);
    int t = 1; cin >> t;
    while (t--)solve();
}
