using namespace std;

#include "cstdio"
#include "iostream"
#include "algorithm"
#include "vector"
#include "string"
#include "cctype"
#include "set"

typedef long long Long;

int main()
{
    string line;
    getline( cin, line );

    int k;
    cin >> k;

    line += string( k, '?' );

    int ans = 0;

    for (int n = 1; 2 * n <= line.length(); n++) {

        for (int i = 0; i + 2 * n <= line.length(); i++) {
            bool ok = true;

            for (int k = 0; k < n; k++) {
                if ( line[i + k] == '?' ) continue;
                if ( line[i + k + n] == '?' ) continue;
                if ( line[i + k] == line[i + k + n] ) continue;

                ok = false;
                break;
            }

            if ( ok ) {
                ans = max( ans, 2 * n );
                break;
            }
        }
    }

    cout << ans << endl;

    return 0;
}
