#include<cstdio>
#include<cstring>
#include<algorithm>

using namespace std;

typedef long long LL;

#define LLF "%I64d"

const int MAX_SIZE = 100000;

int n, data[MAX_SIZE + 10];
LL a[MAX_SIZE + 10], b[MAX_SIZE + 10];


int main(){
	scanf("%d", &n);
	for(int i = 1; i <= n; i ++)
		scanf("%d", data + i);
	for(int i = 1; i <= n; i ++)
		a[i] = a[i - 1] + data[i];
	sort(data + 1, data + n + 1);
	for(int i = 1; i <= n; i ++)
		b[i] = b[i - 1] + data[i];
	int m;
	scanf("%d", &m);
	while(m --){
		int t, l, r;
		scanf("%d%d%d", &t, &l, &r);
		if(t == 1)
			printf(LLF "\n", a[r] - a[l - 1]);
		else
			printf(LLF "\n", b[r] - b[l - 1]);
	}

	return 0;
}
