int cnt1[500], cnt2[500];

void read(int * cnt)
{
	string line, word;
	getline(cin, line);

	istringstream is(line);
	while (is >> word)
	{
		for (int i = 0; i < word.size(); ++i)
			if (isalpha(word[i])) ++cnt[word[i]];
	}
}

int main()
{
	map<string, int> m1, m2;
	read(cnt1);
	read(cnt2);

	bool ok = true;
	FOR(i, 0, 500)
		if (cnt1[i] < cnt2[i]) ok = false;

	if (ok) cout << "YES" << endl;
	else cout << "NO" << endl;

	return 0;
}
