// LUOGU_RID: 110066180
#include<bits/stdc++.h>
#define int long long
const int N=1e5+5,M=1005;
using namespace std;
int n,a[M][M],k,sum;
void solve()
{
	cin>>n>>k;sum=0;
	for(int i=1;i<=n;i++)
	for(int j=1;j<=n;j++)
	cin>>a[i][j];
	for(int i=1;i<=n;i++)
	for(int j=1;j<=n;j++)
	if(a[i][j]!=a[n-i+1][n-j+1]){sum++;a[n-i+1][n-j+1]=a[i][j];}
	if(sum>k||(((k-sum)&1)&&n%2==0))cout<<"No\n";
else cout<<"Yes\n";
}
int tt;
signed main()
{
	ios::sync_with_stdio(false);cin.tie(0);cout.tie(0);
	tt=1;
	cin>>tt;
	while(tt--)solve();
}
