#include<iostream>
#include<algorithm>
#include<cstring>
#include<cstdio>
using namespace std;
typedef long long LL;
const LL Max=1000000000ll;
LL calc(LL a,LL b,LL c,LL s)
{
    LL i,ans=1;
    for(i=1;i<=a;i++)ans*=s;
    ans=ans*b+c;
    return ans;
}
bool Check(LL x,LL s)
{
    LL t=0;
    while(x)
    {
        t+=x%10;
        x/=10;
    }
    return (t==s);
}
void Solve()
{
    LL a,b,c,s,x;
    LL ans[100]={0};
    cin>>a>>b>>c;
    for(s=1;s<=99;s++)
    {
        x=calc(a,b,c,s);
        if(x<Max&&x>0&&Check(x,s))ans[++ans[0]]=x;
    }
    sort(ans+1,ans+ans[0]+1);
    cout<<ans[0]<<endl;
    for(x=1;x<=ans[0];x++)cout<<ans[x]<<' ';
}
int main()
{
    Solve();
    return 0;
}
