#include<cstdio>
#include<cstring>
#include<algorithm>
using namespace std;
const int maxn = 100009;
long long a[maxn], b[maxn];
int main()
{
    int n, i;
    long long ans;
    while(scanf("%d", &n) != EOF)
    {
        for(i=0; i<n; ++i) scanf("%I64d", a+i);
        ans=0;
        for(i=1; i<n; ++i)
        {
            a[i] += ans;
            if(a[i]>=a[i-1]) continue;
            ans += a[i-1]-a[i];
            a[i]=a[i-1];
        }
        printf("%I64d\n", ans);
    }
    return 0;
}
