#include<bits/stdc++.h>
using namespace std;
const double eps(1e-8);
const double pi(3.14159265358979);
const int N=110;
int n,a[N]={};
int main()
{	
	cin>>n;
	for(int i=1;i<=n;++i)
		cin>>a[i];
	for(int i=n;i>=1;--i)
		for(int j=1;j<i;++j)
			if(a[j+1]<a[j])
			{
				cout<<j<<' '<<j+1<<endl;
				swap(a[j],a[j+1]);
			}
	return 0;
}
