#include <bits/stdc++.h>
#define ld long double
#define ll long long int
#define st first
#define nd second
#define pb push_back
#define pii pair<int,int>
#define mp make_pair

using namespace std;

const int N = 1e5 + 5;
int a[N];
int n;

void solve(){
    cin >> n;
    for(int i=1;i<=n;i++) cin >> a[i];
    int g = 0;
    for(int i=1;i<=n;i++){
        int id1 = i;
        int id2 = n + 1 - i;
        if(id2 <= id1) break;
        if(a[id1] == a[id2]) continue;
        int dif = abs(a[id1] - a[id2]);
        g = __gcd(g, dif);
    }
    cout << g << "\n";
}

int main(){
    ios_base::sync_with_stdio(0); cin.tie(0);

    int tt = 1;
    cin >> tt;
    while(tt--) solve();

    return 0;
}
