#pragma GCC optimize("Ofast,unroll-loops")
#pragma GCC target("avx,avx2,fma")
#include<bits/stdc++.h>
#define int long long
const int N=2e5+5,M=305;
using namespace std;
int n,m,b[N],sum;
void solve()
{
	cin>>n>>m;sum=0;if(n>m)swap(n,m);
	for(int i=1;i<=n*m;i++)cin>>b[i];
	sort(b+1,b+n*m+1);
	int maxn=b[n*m],maxn2=b[n*m-1];
	sum=max((maxn-b[2])*(n-1)+(n*m-n)*(maxn-b[1]),
			(maxn2-b[1])*(n-1)+(n*m-n)*(maxn-b[1]));
	cout<<sum<<'\n';
}
int tt;
signed main()
{
	ios::sync_with_stdio(false);cin.tie(0);cout.tie(0);
	tt=1;
	cin>>tt;
	while(tt--)solve();
}
