#include <iostream>
#include <cstdio>
#include <cstring>
#include <cmath>
#include <string>
#include <vector>

using namespace std;

typedef long long LL;

const LL INF = 100000000000000000LL;

LL w, M, k;
LL ans;

LL calcS(LL x)
{
	if (x < 10) return x;
	LL ret = 0;
	LL i = 1, bit = 1;
	for (; i * 10 <= x; i *= 10, ++bit) ret += bit * i * 9;
	LL digit = x / i % 10;
	ret += (digit - 1) * i * bit;
	ret += (x % i + 1)* bit;
	return ret;
}

int main()
{
	cin >> w >> M >> k;
	for (LL l = 0, r = INF - M; l <= r;) {
		LL m = (l + r) / 2;
		if ((calcS(M + m - 1) - calcS(M - 1)) <= w / k) ans = m, l = m + 1;
		else r = m - 1;
	}
	cout << ans << endl;
	return 0;
}
