using namespace std;

int n, a[30];
int main()  {
	scanf("%d",&n);
	for (int i=1;i<=n;i++)scanf("%d",&a[i]);
	sort(a+1,a+1+n);
	long long ans=a[n];
	for (int i=n-1;i>0;i --){
		while (a[i] != 0 && a[i] >= a[i + 1]) {
			a[i] --;
		}
		ans+=a[i];
	}
	cout<<ans<<endl;
	return 0;
}
