typedef double DB;
int n;

int main(){
    DB a, d;
    scanf("%lf%lf", &a, &d);
    scanf("%d", &n);
    d = d - (int)(d / 4 / a) * (4 * a);
    int dir = 0;
    DB x = 0, y = 0;
    while (n--){
        DB t = d;
        for (int i = 0; i < 5; i++)
        if (dir == 0){
            if (x + t < a){
                x += t;
                break;
            }
            t -= (a - x);
            x = a;
            dir = 1;
        }else if (dir == 1){
            if (y + t < a){
                y += t;
                break;
            }
            t -= (a - y);
            y = a;
            dir = 2;
        }else if (dir == 2){
            if (x > t){
                x -= t;
                break;
            }
            t -= x;
            x = 0;
            dir = 3;
        }else{
            if (y > t){
                y -= t;
                break;
            }
            t -= y;
            y = 0;
            dir = 0;
        }
        printf("%.6lf %.6lf\n", x, y);
    }
}
