#include <stdio.h>
int n, a, b, x[100000];
int main()
{
	scanf("%d%d%d", &n, &a, &b);
	for(int i = 0; i < n; i++)
		scanf("%d", &x[i]);
	for(int i = 0; i < n; i++)
	{
		long long tmp = (long long)x[i] * a / b;
		printf("%d ", x[i] - (tmp * b % a ? tmp * b / a + 1 : tmp * b / a));
	}
	getchar(); getchar();
	return 0;
}
