#include <bits/stdc++.h>
#define int long long
using namespace std;
void mermermer()
{
	string a,b;
	cin >> a >> b;
	string c;
	swap(a,b);
	for(int i=1;i<=a.size()-b.size();i++) c+='0';
	b=c+b;
	while(a.size()&&b.size()&&a[0]==b[0])
	{
		a.erase(a.begin());
		b.erase(b.begin());
	}
	if(a.empty()) cout << "0\n";
	else
	{
		cout << (a.size()-1)*9+(a[0]-b[0]) << "\n";
	}
}
signed main()
{
	ios::sync_with_stdio(false);
	cin.tie(0);
	int T;
	cin >> T;
	while(T--) mermermer();
	return 0;
}
