int T,n;
int x[100005],t[100005];
bool check(int cur){
	int lb=-2e9,rb=2e9;
	for(int i=1;i<=n;i++){
		lb=max(lb,x[i]-(cur-t[i]));
		rb=min(rb,x[i]+(cur-t[i]));
	}
	return lb<=rb;
}
