#include <iostream>
#include <algorithm>
using namespace std;

int main() {
	int n, A, B, C, T, t;
	scanf("%d%d%d%d%d", &n, &A, &B, &C, &T);
	long long ans = 0;
	for (int i = 1; i <= n; i++) {
		scanf("%d", &t);

		if (C >= B) {
			ans += A + (long long)(T - t) * (C - B);
		}
		else ans += A;
	}
	cout << ans << endl;
	return 0;
}
