using namespace std;
void BellaKira()
{
	int n;
	cin>>n;
	int x=0,y=0,z=0;
	for (int i=1;i<=n;i++)
	{
		int o=0;
		cin>>o;
		if (o==0) x++;
		else if (o==1) y++;
		else z++;
	}
	if (y+z>=x-1)
	{
		cout<<0<<'\n';
		return;
	}
	if (z)
	{
		cout<<1<<'\n';
		return;
	}
	if (!y) cout<<1<<'\n';
	else
		cout<<2<<'\n';
}
signed main()
{
	IOS;cin.tie(0); 
	int T=1;
	cin>>T;
	while (T--)
	{
		BellaKira();
	}
}
