#include <bits/stdc++.h>
#define ll long long
using namespace std;
	int n,a,minh;
	double ptr,mina=720;
int inline read()
{
    int x=0,f=1;char ch=getchar();
    while(ch<'0'||ch>'9'){if(ch=='-')f=-1;ch=getchar();}
    while(ch>='0'&&ch<='9'){x=x*10+ch-'0';ch=getchar();}
    return x*f;
}
int main()
{
	n=read(),a=read();
	ptr=(double)180/n;
	for (int i=1;i<n-1;i++)
		if (fabs(ptr*i-a)<mina) mina=fabs(ptr*i-a),minh=i;
	printf("%d %d %d\n",1,2+minh,1+minh);
	return 0;
}
