using namespace std;
#define N 1000002
typedef long long ll;
int n;
int main()
{
	scanf("%d",&n);
	if(n&1)
	{
		n=(n+1)/2;
		ll ans=n*(n+1)/2;
		printf("%lld\n",ans*4);
	}
	else
	{
		printf("%lld\n",1ll*(n/2+1)*(n/2+1));
	}
}
