using namespace std;
#define all(v) (v).begin(), (v).end()
#define rall(v) (v).rbegin(), (v).rend()
#define pb push_back
#define f(i,x,y) for(int i = x; i<y; i++ )
#define FORV(it,A) for(vector<int>::iterator it = A.begin(); it!= A.end(); it++)
#define FORS(it,A) for(set<int>::iterator it = A.begin(); it!= A.end(); it++)
#define quad(x) (x) * (x)
#define mp make_pair
#define clr(x, y) memset(x, y, sizeof x)
#define fst first
#define snd second
#define eps 1e-7
typedef pair<int, int> pii;
typedef long long ll;
typedef long double ld;

int main() {
	ll x, y, n, a, b = -1;
	cin >> x >> y >> n;
	for (ll d = 1; d <= n; d++) {
		ll ini = 0, fim = n*x, m1, m2;
		while (ini+2 < fim) {
			m1 = ini + (fim - ini)/3;
			m2 = fim - (fim - ini)/3;
			if (abs(x*d - m1*y) < abs(x*d - m2*y)) fim = m2;
			else ini = m1;
		}
		ll val;
		if (abs(x*d - ini*y) <= abs(x*d - (ini+1)*y) && abs(x*d - ini*y) <= abs(x*d - fim*y)) val = ini;
		else if (abs(x*d - (ini+1)*y) <= abs(x*d - fim*y)) val = ini+1;
		else val = fim;

		if (b == -1 || b*abs(x*d - val*y) < d*abs(x*b - y*a)) {
			b = d;
			a = val;
		}	
	}
	cout << a << '/' << b << endl;
	return 0;
}
