#include<bits/stdc++.h>
#define pb push_back
#define eb emplace_back
#define SZ(x) ((int)(x).size())
#define each(x,v) for(auto&x:v)
#define rep(i,a,b) for(int i=(a);i<=(b);++i)
#define per(i,a,b) for(int i=(a);i>=(b);--i)
using namespace std;
int T;
int main(){
	scanf("%d",&T);
	while(T--){
		int n,m;
		scanf("%d%d",&n,&m);
		vector<tuple<int,int,int> >v;
		rep(i,0,n-1)rep(j,0,m-1){
			v.eb(max({i+j,n-1-i+m-1-j,n-1-i+j,i+m-1-j}),i,j);
		}
		sort(v.begin(),v.end());
		each(x,v)printf("%d ",get<0>(x));
		puts("");
	}
	return 0;
}
