typedef long long ll;
using namespace std;
void gn(int &x) {
	x = 0; char ch = getchar();
	while (ch < '0' || ch > '9') ch = getchar();
	while (ch >= '0' && ch <= '9')
		x = x * 10 + ch - '0', ch = getchar();
}
int m, b;
ll ans;
int main() {
	gn(m); gn(b);
	for (int y = 0; y <= b; ++y) {
		int x = m * (b - y);
		ans = max(ans, 1ll * x * (x + 1) / 2ll * (y + 1) + 1ll * y * (y + 1) / 2ll * (x + 1));
	}
	printf("%I64d\n", ans);
	return 0;
