#include<cstdio>
#include<cstring>
#include<cmath>
#include<algorithm>
using namespace std;
int n,m,k;
int r1[10000],p1[10000],p2[10000];
int main()
{
    while(~scanf("%d",&n))
    {
        for(int i=1;i<=n;i++)scanf("%d",&r1[i]);
        scanf("%d",&m);
        for(int i=1;i<=m;i++)scanf("%d",&p1[i]);
        scanf("%d",&k);
        for(int i=1;i<=k;i++)scanf("%d",&p2[i]);
        int A,B;
        scanf("%d %d",&A,&B);
        sort(r1+1,r1+1+n);
        sort(p1+1,p1+1+m);
        sort(p2+1,p2+1+k);
        double ans=(double)p2[1]/p1[m];
        ans*=A;
        ans/=B;
        ans+=1;
        ans=1.0/ans;
        ans=sqrt(ans);
        ans*=r1[n];
        printf("%.10f\n",ans);
    }
}
