#include <bits/stdc++.h>
#include <iostream>
#include <set>
using namespace std;
#define mp make_pair
#define fi first
#define se second
typedef long long ll;
ll x[4],y[4];
set<pair<ll,ll> >s;
int main()
{
	for(ll i=1;i<=3;i++)
		cin>>x[i]>>y[i];
	for(ll i=1;i<=3;i++)
		for(ll j=1;j<=3;j++)
			if(i!=j)
				s.insert(mp(x[6-i-j]+x[j]-x[i],y[6-i-j]+y[j]-y[i]));
	cout<<s.size()<<endl;
	for(auto x:s)
		cout<<x.fi<<" "<<x.se<<endl;
}
